    public void Reset() {
        origMatrix = new Complex[imgMats[imgSel].GetLength(0),imgMats[imgSel].GetLength(1)];
        // quick and dirty copy ctor
        for (int i = 0; i < imgMats[imgSel].GetLength(0); i++)
        {
            for (int j = 0; j < imgMats[imgSel].GetLength(1); j++)
            {
                origMatrix[i, j].real = imgMats[imgSel][i,j].real;
                origMatrix[i, j].imag = imgMats[imgSel][i, j].imag;
            }
        }
        UpdateForward();
    }

    void UpdateForward()
    {
        UpdateTable("OrigTable", origMatrix);
        UpdateBoard("OrigTable", origMatrix);
        fftMatrix = FFT.FFT2D(origMatrix, origMatrix.GetLength(0), origMatrix.GetLength(1), 1);
        UpdateTable("FourierTable", fftMatrix);
        UpdateBoard("FourierTable", fftMatrix);
    }

    void UpdateBackward()
    {
        UpdateTable("FourierTable", fftMatrix);
        UpdateBoard("FourierTable", fftMatrix);
        origMatrix = FFT.FFT2D(fftMatrix, origMatrix.GetLength(0), origMatrix.GetLength(1), -1);
        UpdateTable("OrigTable", origMatrix);
        UpdateBoard("OrigTable", origMatrix);
    }

    void UpdateTable(string tableName, Complex[,] tableMat)
    {
        GameObject.Find(tableName).GetComponent<TableGraph>().matrix = tableMat;
        GameObject.Find(tableName).GetComponent<TableGraph>().is_init = false;
    }

    public void UpdateBoard(string tableName, Complex[,] tableMat)
    {
        GameObject.Find(tableName).GetComponent<BoardGraph>().matrix = tableMat;
        GameObject.Find(tableName).GetComponent<BoardGraph>().is_init = false;
    }

    public void AddNoise()
    {
        Debug.Log("Mutate");
        // This doesn't actually permute. It mutates (adds noise)
        Tools.Complex.Permute(origMatrix);
        UpdateForward();
    }

    public void Sharpen()
    {
        // compute edge sharpened fft
        Debug.Log("Sharpen");
        for (int i = 0; i < fftMatrix.GetLength(0); i++)
        {
            for (int j = 0; j < fftMatrix.GetLength(1); j++)
            {
                fftMatrix[i, j] = fftMatrix[i, j] *
                    (1 + a * System.Math.Pow((2 * System.Math.PI * i / fftMatrix.GetLength(0)), 2)
                        + a * System.Math.Pow((2 * System.Math.PI * j / fftMatrix.GetLength(1)), 2)) / (1 + a);
            }
        }
        UpdateBackward();
    }
    public void Normalize()
    {
        // linear normalize
        Debug.Log("Normalize");
        double max = 0;
        for (int i = 0; i < origMatrix.GetLength(0); i++)
        {
            for (int j = 0; j < origMatrix.GetLength(1); j++)
            {
                max = System.Math.Max((double)max, origMatrix[i, j].real);
            }
        }
        Debug.Log("max is: " + max);    
        if (max < 255) return;
        for (int i = 0; i < origMatrix.GetLength(0); i++)
        {
            for (int j = 0; j < origMatrix.GetLength(1); j++)
            {
                origMatrix[i, j].real = origMatrix[i,j].real * 255d / max;
                origMatrix[i, j].imag = origMatrix[i, j].real * 255d / max;
            }
        }
        UpdateForward();
    }
    public void ChgFwd()
    {
        Debug.Log("Change img foreward");
        imgSel = (imgSel+1)%imgMats.Count;
        Reset();
    }
    public void ChgBwd()
    {
        Debug.Log("Change img back");
        imgSel = (imgSel + imgMats.Count-1) % imgMats.Count;
        Reset();
    }

